@import '_variables';

@mixin box-shadow( $horiz : -.2em , $vert : .2em , $blur : 10px , $spread : 4px , $color : #242424d2 ){
    -webkit-box-shadow: $horiz $vert $blur $spread $color;
    -moz-box-shadow: $horiz $vert $blur $spread $color;
    box-shadow: $horiz $vert $blur $spread $color;
}

